package Lec511jan;

public class Q31 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int row = 1;
		int nst = 5;
		
		
		while(row<=5) {
			
			int i = 1;
			int count = 5;
			while(i<=nst) {
				System.out.print(count+ " ");
				count--;
				i = i +1;
				
				
				
				
			}
			
			
			
			
			
			row++;
			System.out.println();
		}

	}

}
